<?phpclass Route {    function __construct() {		}		public static function Start(){			//карта сайта, имя контьролерра и функций в нём, можно решить добавлением каждой функции в массив        $map = array("404"   => array("index"=>1),					 "index" => array("index"=>1),		             "news"  => array("show" =>1,  		                             "topic" =>1,									 "autor" =>1),					 "admin" => array("index"=>1,									  "topic"=>1,									  "del"  =>1,									  "add"  =>1,									  "addnew"=>1,									  "exit"  =>1,									  "update"=>1)									  ); 		    //echo $map;		//назначаем параметр по умолчанию		$controller_name = 'index';		$action_name = 'index';				//преобразуем строку запроса в массив		$route_array = explode ('/',$_SERVER['REQUEST_URI']);				if(!empty($route_array[1])){		$controller_name = $route_array[1];		}				if(!empty($route_array[2])){		$action_name = $route_array[2];		}				//проверяет валидность контролера и модели в карте маршрутизации		$map_valid = $map[$controller_name][$action_name];		if($map_valid!=1){		 header ('Location: /404'); // ставит в строке браузера 404		 exit;		}				//добавляем префиксы		$model_name = 'Model_'.$controller_name;		$controller_name = 'Controller_'.$controller_name;		$action_name = 'Action_'.$action_name;        //echo "controller_name".$controller_name;						// проверяем есть ли такой файл если есть то подключаем		if(file_exists (Q_PATH.'/application/models/'.$model_name.'.php')){		    include Q_PATH.'/application/models/'.$model_name.'.php';				}		if (file_exists (Q_PATH.'/application/controllers/'.$controller_name.'.php')) {		include Q_PATH.'/application/controllers/'.$controller_name.'.php';				}		else {		 header ('Location: /404'); // ставит в строке браузера 404		 exit;		}						//создаёт экземпляр класса контролер который запрашивается		$controller = new $controller_name();		$controller->$action_name(); //запускает функцию экшн в экземпляре созданного класса	}		}